import React, { useState } from 'react';
import './style.css';

function Location() {
  const [searchTerm, setSearchTerm] = useState('');
  const [results, setResults] = useState([]);
  const [error, setError] = useState('');

  const handleSearch = async () => {
    try {
      const response = await fetch(`/api/locations/search?name=${searchTerm}`);
      
      // Check if the response is JSON
      const contentType = response.headers.get('content-type');
      if (!contentType || !contentType.includes('application/json')) {
        throw new Error('Response is not JSON');
      }

      const data = await response.json();
      
      if (Array.isArray(data) && data.length === 0) {
        setError('No locations found with that name.');
        setResults([]);
      } else if (Array.isArray(data)) {
        setError('');
        setResults(data);
      } else {
        setError('Invalid response received.');
        setResults([]);
      }
    } catch (error) {
      setError('Error fetching search results.');
      console.error('Error fetching search results:', error);
      setResults([]);
    }
  };

  return (
    <div>
      <section className="home">
        <div className="home-box">      
          <div className="content">
            <h4>Travel</h4>
            <h1>Let's start your dream journey with .......</h1>
            <div className="search">
              <i className="fa fa-search"></i>
              <input 
                type="text" 
                placeholder="Your journey begins with a search..." 
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
              <button onClick={handleSearch}>Search</button>
            </div>
            {error && <p className="error-message">{error}</p>}
          </div>
        </div>
      </section>

      <section className="destination">
        <div className="container">
          {results.map((location) => (
            <div key={location._id} className="gallery">
              <div className="box">
                <img src={`http://localhost:5000/${location.picture}`} alt={location.name} />
              </div>
              <div className="container-box">
                <h2 className="heading">{location.name} - {location.city}</h2>
                <div className="content">
                  <p>{location.description}</p>
                  <a href="#">Explore more <i className="fa-solid fa-arrow-right"></i></a>
                </div>
              </div>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
}

export default Location;
