import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const AdminLogin = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleUsernameChange = (e) => {
    const value = e.target.value;
    const isValid = /^[A-Za-z]+$/.test(value);

    if (isValid) {
      setError('');
    } else {
      setError('Username should only contain characters.');
    }

    setUsername(value);
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    if (error) {
      return;
    }

    const admin = { 
      username, 
      password
    };

    try {
      const response = await axios.post('http://localhost:8081/login', admin);
      const { token, role } = response.data;
      localStorage.setItem('token', token); // Store the token in local storage

      switch (role.trim()) {
        case 'location_manager':
          navigate('/LocationAdmin/home');
          break;
        case 'hotelmanager':
          navigate('/hotelmanager/home');
          break;
        case 'admin':
          navigate('/admin/home');
          break;
        case 'usersupporter':
          navigate('/usersupporter/home');
          break;
        case 'scheduler':
          navigate('/scheduler/home');
          break;
        case 'travelagent':
          navigate('/travelagent/home');
          break;
        case 'vehiclerentle':
          navigate('/vehiclerentle/home');
          break;
        default:
          setError('Unknown role');
      }
    } catch (error) {
      if (error.response && error.response.status === 401) {
        setError('Invalid username or password');
      } else {
        setError('An error occurred. Please try again later.');
      }
    }
  };

  return (
    <div className="container mt-5">
      <h2>Login</h2>
      <form onSubmit={handleLogin}>
        <div className="form-group">
          <label htmlFor="exampleInputEmail1">User Name</label>
          <input
            type="text"
            className="form-control"
            id="exampleInputEmail1"
            aria-describedby="emailHelp"
            placeholder="Enter User Name"
            value={username}
            onChange={handleUsernameChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="exampleInputPassword1">Password</label>
          <input
            type="password"
            className="form-control"
            id="exampleInputPassword1"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        {error && <p className="text-danger">{error}</p>}
        <button type="submit" className="btn btn-primary">Submit</button>
      </form>
    </div>
  );
};

export default AdminLogin;
