import React from 'react';
import { useHistory } from 'react-router-dom'; // For navigation
import './Header.css'; // Import the custom CSS file

const Header = () => {
    const history = useHistory(); // Use useHistory to programmatically navigate

    const handleLogout = () => {
        // Remove token from localStorage
        localStorage.removeItem('token');
        
        // Optionally, you might want to clear other session-related data
        // localStorage.removeItem('otherKey');

        // Redirect to login page
        history.push('/admin/login');
    };

    return (
        <ul className="nav nav-underline bg-light d-flex header-nav">
            <li className="nav-item">
                <a className="nav-link" aria-current="page" href="/LocationAdmin/home">DashBoard</a>
            </li>
            <li className="nav-item">
                <a className="nav-link" aria-current="page" href="/LocationAdmin/LocationsSummary">Reacts & Comments</a>
            </li>
            <li className="nav-item ml-auto">
                <a className="nav-link logout-link" href="#" onClick={handleLogout}>Logout</a>
            </li>
        </ul>
    );
}

export default Header;
