import React, { useState } from 'react';
import './style.css';

function Location() {
  const [searchTerm, setSearchTerm] = useState('');
  const [results, setResults] = useState([]);
  const [placeholder, setPlaceholder] = useState('Your journey begins with a search...');

  const handleSearch = async () => {
    if (!searchTerm.trim()) {
      setPlaceholder('Please enter a valid search term.');
      return;
    }

    try {
      const response = await fetch(`/api/locations/search?name=${searchTerm}`);
      const data = await response.json();
      if (data.length === 0) {
        setPlaceholder('No results found. Try a different search term.');
      } else {
        setResults(data);
      }
    } catch (error) {
      console.error('Error fetching search results:', error);
      setPlaceholder('An error occurred. Please try again.');
    }
  };

  return (
    <div>
      <section className="home">
        <div className="home-box">      
          <div className="content">
            <h4>Travel</h4>
            <h1>Let's start your dream journey with .......</h1>
            <div className="search">
              <i className="fa fa-search"></i>
              <input 
                type="text" 
                placeholder={placeholder} 
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                onFocus={() => setPlaceholder('Your journey begins with a search...')}
              />
              <button onClick={handleSearch}>Search</button>
            </div>
          </div>
        </div>
      </section>

      <section className="destination">
        <div className="container">
          {results.map((location) => (
            <div key={location._id} className="gallery">
              <div className="box">
                <img src={location.picture} alt={location.name} />
              </div>
              <div className="container-box">
                <h2 className="heading">{location.name} - {location.city}</h2>
                <div className="content">
                  <p>{location.description}</p>
                  <a href="#">Explore more <i className="fa-solid fa-arrow-right"></i></a>
                </div>
              </div>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
}

export default Location;
