import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const AdminLogin = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    const admin = {
      username,
      password,
      
  }
    
      const response = await axios.post('http://localhost:8081/api/login', admin);
      const role = response.data.username; // Extract role from the response

      switch (role) {
        case 'locationmanager':
          navigate('/locationmanager/home');
          break;
        case 'hotelmanager':
          navigate('/hotelmanager/home');
          break;
        case 'admin':
          navigate('/admin/home');
          break;
        case 'usersupporter':
          navigate('/usersupporter/home');
          break;
        case 'uthara':
          navigate('/scheduler/home');
          break;
        case 'travelagent':
          navigate('/travelagent/home');
          break;
        default:
          setError('Unknown role');
      }
  
  };

  return (
    <div>
      <h2>Admin Login</h2>
      <form onSubmit={handleLogin}>
        <div>
          <label>Username</label>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Password</label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        {error && <p style={{ color: 'red' }}>{error}</p>}
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default AdminLogin;
