import React from 'react';
import { useNavigate } from 'react-router-dom'; // Use useNavigate instead of useHistory
import './Header.css'; // Import the custom CSS file

const Header = () => {
    const navigate = useNavigate(); // Use useNavigate to programmatically navigate

    const handleLogout = () => {
       
        localStorage.removeItem('token');
        navigate('/admin/login');
    };

    return (
        <dev className= "navbar-custom">
            <ul >
            <li >
                <a aria-current="page" href="/LocationAdmin/home">DashBoard</a>
            </li>
            <li>
                <a  aria-current="page" href="/LocationAdmin/LocationsSummary">Reacts & Comments</a>
            </li>
            <li>
                <a className="nav-link" aria-current="page" href="/LocationAdmin/profile">Profile</a>
            </li>
            <li >
                <button className="nav-link logout-link" href="#" onClick={(e) => { e.preventDefault(); handleLogout(); }}>Logout</button>
            </li>
        </ul>
        </dev>
        
    );
}

export default Header;
