import React from 'react';
import { useNavigate } from 'react-router-dom'; // Use useNavigate instead of useHistory
import './Header.css'; // Import the custom CSS file

const Header = () => {
    const navigate = useNavigate(); // Use useNavigate to programmatically navigate

    const handleLogout = () => {
        // Remove token from localStorage
        localStorage.removeItem('token');
        
        // Optionally, you might want to clear other session-related data
        // localStorage.removeItem('otherKey');

        // Redirect to login page
        navigate('/admin/login');
    };

    return (
        <dev className= "navbar-custom">
            <ul >
            <li className="menu-item">
                <a className="nav-link" aria-current="page" href="/LocationAdmin/home">DashBoard</a>
            </li>
            <li className="menu-item">
                <a className="nav-link" aria-current="page" href="/LocationAdmin/LocationsSummary">Reacts & Comments</a>
            </li>
            <div className="profile">
            <li className="nav-item ml-auto">
                <button className="nav-link profile" aria-current="page" href="/LocationAdmin/profile">Profile</button>
            </li>
            <li className="nav-item ml-auto">
                <button className="nav-link logout-link" href="#" onClick={(e) => { e.preventDefault(); handleLogout(); }}></button>
            </li>
            </div>
            
        </ul>
        </dev>
        
    );
}

export default Header;
