import React, { useState, useEffect } from 'react';

function LocationsSummary() {
  const [locations, setLocations] = useState([]);

  useEffect(() => {
    const fetchLocationsSummary = async () => {
      try {
        const response = await fetch('http://localhost:8081/Locationlocations-summary', {
          method: 'GET',
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`,
          },
        });
        const data = await response.json();
        if (response.ok) {
          setLocations(data);
        } else {
          console.error('Error fetching locations summary:', response.statusText);
        }
      } catch (error) {
        console.error('Error fetching locations summary:', error);
      }
    };

    fetchLocationsSummary();
  }, []);

  return (
    <div>
      <h2>Locations Summary</h2>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Likes</th>
            <th>Comments</th>
          </tr>
        </thead>
        <tbody>
          {locations.map(location => (
            <tr key={location._id}>
              <td>{location.name}</td>
              <td>{location.likes}</td>
              <td>
                <ul>
                  {location.comments.map((comment, index) => (
                    <li key={index}>{comment.text}</li>
                  ))}
                </ul>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default LocationsSummary;
