import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './LoginPage.css';

const handleLogin = async (credentials, navigate) => {
  try {
    const response = await fetch('http://localhost:8081/userauth/login', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(credentials),
    });

    const data = await response.json();
    if (data.token) {
      localStorage.setItem('token', data.token);
      navigate('/home'); // Navigate to /home after successful login
    } else {
      alert('Login failed: ' + data.message); // Display error message
    }
  } catch (error) {
    console.error('Error logging in:', error);
    alert('An error occurred. Please try again.');
  }
};

function LoginPage() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate(); // React Router's useNavigate hook

  const handleSubmit = async (event) => {
    event.preventDefault();
    await handleLogin({ username, password }, navigate);
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <h2>Welcome</h2>
        <div className="profile-image">
          <img src="https://via.placeholder.com/100" alt="Profile" />
        </div>
        <form onSubmit={handleSubmit}>
          <div className="input-group">
            <label>Username</label>
            <input
              type="text"
              placeholder="Enter your username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
          </div>
          <div className="input-group">
            <label>Password</label>
            <input
              type="password"
              placeholder="Enter your password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
          <div className="forgot-password">
            <a href="#">Forgot your password?</a>
          </div>
          <button type="submit" className="login-btn">LOGIN</button>
          <div className="signup-link">
            <span>Donâ€™t have an account?</span> <a href="#">SIGN UP</a>
          </div>
        </form>
      </div>
    </div>
  );
}

export default LoginPage;
