import React, { useState } from 'react';
import axios from 'axios';
import './agency.css';

const Agency = () => {
  const [formData, setFormData] = useState({
    agencyName: '',
    phoneNumber: '',
    email: '',
    location: '',
    places: [''],
    maxPeople: '',
    price: '',
    image: null,
  });

  const [errors, setErrors] = useState({});

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevFormData) => ({
      ...prevFormData,
      [name]: value,
    }));
  };

  const handlePlaceChange = (index, value) => {
    const regex = /^[A-Za-z\s]+$/;
    const newPlaces = [...formData.places];
    if (value === '' || regex.test(value)) {
      newPlaces[index] = value;
      setFormData((prevFormData) => ({
        ...prevFormData,
        places: newPlaces,
      }));
      setErrors((prevErrors) => ({ ...prevErrors, [`place${index}`]: '' }));
    } else {
      setErrors((prevErrors) => ({
        ...prevErrors,
        [`place${index}`]: 'Place must contain only letters and spaces.',
      }));
    }
  };

  const handleImageChange = (e) => {
    setFormData((prevFormData) => ({
      ...prevFormData,
      image: e.target.files[0],
    }));
  };

  const addPlace = () => {
    setFormData((prevFormData) => ({
      ...prevFormData,
      places: [...prevFormData.places, ''],
    }));
  };

  const removePlace = (index) => {
    const newPlaces = [...formData.places];
    newPlaces.splice(index, 1);
    setFormData((prevFormData) => ({
      ...prevFormData,
      places: newPlaces,
    }));
    setErrors((prevErrors) => {
      const newErrors = { ...prevErrors };
      delete newErrors[`place${index}`];
      return newErrors;
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Perform any additional validation if necessary here.

    const data = new FormData();
    for (const key in formData) {
      if (key === 'places') {
        formData[key].forEach((place, index) => {
          data.append(`places[${index}]`, place);
        });
      } else {
        data.append(key, formData[key]);
      }
    }

    try {
      const response = await axios.post('http://localhost:8081/package/create', data, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      // Reset form after successful submission
      setFormData({
        agencyName: '',
        phoneNumber: '',
        email: '',
        location: '',
        places: [''],
        maxPeople: '',
        price: '',
        image: null,
      });

      console.log('Form submitted successfully:', response.data);
    } catch (error) {
      console.error('Error submitting the form', error);
    }
  };

  return (
    <>
      <div className="travelagency">
        <h1>Travel Packages</h1>
      </div>
      <div className="traveltitle">
        <h1>Welcome Travel Agencies!</h1>
        <p>Customize the travel packages</p>
      </div>
      <form onSubmit={handleSubmit}>
        <div className="m1">
          <label>Name of the Travel Agency</label>
          <input
            type="text"
            className="form-control"
            name="agencyName"
            value={formData.agencyName}
            onChange={handleChange}
          />
          {errors.agencyName && <div className="error">{errors.agencyName}</div>}

          <label>Phone Number</label>
          <input
            type="tel"
            className="form-control"
            name="phoneNumber"
            value={formData.phoneNumber}
            onChange={handleChange}
          />
          {errors.phoneNumber && <div className="error">{errors.phoneNumber}</div>}

          <label>Email Address</label>
          <input
            type="email"
            className="form-control"
            name="email"
            value={formData.email}
            onChange={handleChange}
          />
          {errors.email && <div className="error">{errors.email}</div>}
          <div id="emailHelp" className="form-text">
            We'll never share your email with anyone else.
          </div>

          <label>Location</label>
          <input
            type="text"
            className="form-control"
            name="location"
            value={formData.location}
            onChange={handleChange}
          />
          {errors.location && <div className="error">{errors.location}</div>}

          <label>Places of Specific Location</label>
          {formData.places.map((place, index) => (
            <div key={index} className="place-input">
              <input
                type="text"
                className="form-control"
                value={place}
                onChange={(e) => handlePlaceChange(index, e.target.value)}
              />
              {errors[`place${index}`] && <div className="error">{errors[`place${index}`]}</div>}
              {formData.places.length > 1 && (
                <button
                  type="button"
                  onClick={() => removePlace(index)}
                  className="btn btn-danger"
                >
                  Remove
                </button>
              )}
            </div>
          ))}
          <button type="button" onClick={addPlace} className="btn btn-secondary">
            Add Place
          </button>

          <label>Max People</label>
          <input
            type="number"
            className="form-control"
            name="maxPeople"
            value={formData.maxPeople}
            onChange={handleChange}
          />
          {errors.maxPeople && <div className="error">{errors.maxPeople}</div>}

          <label>Price</label>
          <input
            type="number"
            className="form-control"
            name="price"
            value={formData.price}
            onChange={handleChange}
          />
          {errors.price && <div className="error">{errors.price}</div>}

          <label>Upload Image</label>
          <input
            type="file"
            className="form-control"
            name="image"
            onChange={handleImageChange}
          />
        </div>
        <button type="submit" className="btn btn-primary">
          Submit
        </button>
      </form>
    </>
  );
};

export default Agency;
